import admin from 'firebase-admin';

// Uncomment the line below if you are using a Firestore emulator locally.
// process.env['FIRESTORE_EMULATOR_HOST'] = 'localhost:8080';

/**
 * Initializes the Firebase Admin SDK with application default credentials and environment variables
 * for the project ID and storage bucket. This ensures the Admin SDK is initialized only once in a
 * server environment.
 *
 * The Firebase Admin SDK provides access to Firebase services like Authentication, Firestore, and Storage.
 */
if (!admin.apps.length) {
  admin.initializeApp({
    credential: admin.credential.applicationDefault(),
    projectId: process.env.NEXT_PUBLIC_FIREBASE_PROJECT_ID,
    storageBucket: process.env.NEXT_PUBLIC_FIREBASE_STORAGE_BUCKET,
  });
}

/**
 * Admin Auth instance for handling authentication operations via Firebase Admin SDK.
 * 
 * @constant
 */
const adminAuth = admin.auth();

/**
 * Admin Firestore instance for interacting with the Firestore database via Firebase Admin SDK.
 * 
 * @constant
 */
const adminDb = admin.firestore();

/**
 * Admin Storage instance for interacting with Firebase Storage via Firebase Admin SDK.
 * 
 * @constant
 */
const adminStorage = admin.storage();

export { admin, adminAuth, adminDb, adminStorage };

// Import the functions you need from the Firebase SDKs
import { getApp, getApps, initializeApp } from "firebase/app";
import { getAuth, GoogleAuthProvider, signInWithPopup, connectAuthEmulator } from "firebase/auth";
import { getFirestore, doc, setDoc } from "firebase/firestore";
import { getStorage } from "firebase/storage";

/**
 * Firebase configuration object for initializing Firebase in a web app.
 * For Firebase JS SDK v7.20.0 and later, the `measurementId` is optional.
 * Values are pulled from environment variables for security and flexibility.
 *
 * @constant
 * @type {Object}
 * @property {string} apiKey - Firebase API key (from the environment variable NEXT_PUBLIC_FIREBASE_API_KEY)
 * @property {string} authDomain - Firebase Auth domain (from the environment variable NEXT_PUBLIC_FIREBASE_AUTH_DOMAIN)
 * @property {string} projectId - Firebase Project ID (from the environment variable NEXT_PUBLIC_FIREBASE_PROJECT_ID)
 * @property {string} storageBucket - Firebase storage bucket (from the environment variable NEXT_PUBLIC_FIREBASE_STORAGE_BUCKET)
 * @property {string} messagingSenderId - Firebase messaging sender ID (from the environment variable NEXT_PUBLIC_FIREBASE_MESSAGING_SENDER_ID)
 * @property {string} appId - Firebase app ID (from the environment variable NEXT_PUBLIC_FIREBASE_APP_ID)
 */
const firebaseConfig = {
  apiKey: process.env.NEXT_PUBLIC_FIREBASE_API_KEY,
  authDomain: process.env.NEXT_PUBLIC_FIREBASE_AUTH_DOMAIN,
  projectId: process.env.NEXT_PUBLIC_FIREBASE_PROJECT_ID,
  storageBucket: process.env.NEXT_PUBLIC_FIREBASE_STORAGE_BUCKET,
  messagingSenderId: process.env.NEXT_PUBLIC_FIREBASE_MESSAGING_SENDER_ID,
  appId: process.env.NEXT_PUBLIC_FIREBASE_APP_ID
};

/**
 * Initialize Firebase app only if there are no existing Firebase app instances.
 * 
 * @constant
 * @type {FirebaseApp}
 */
const app = getApps().length ? getApp() : initializeApp(firebaseConfig);

/**
 * Firebase Authentication instance for managing authentication-related operations.
 * 
 * @constant
 * @type {Auth}
 */
const auth = getAuth(app);

/**
 * Firestore database instance for interacting with the Firestore database.
 * 
 * @constant
 * @type {Firestore}
 */
const db = getFirestore(app);

/**
 * Firebase Storage instance for managing file storage operations.
 * 
 * @constant
 * @type {Storage}
 */
const storage = getStorage(app);

/**
 * If the Firebase Auth Emulator is enabled (via environment variable), connect to the emulator.
 */
if (process.env.NEXT_PUBLIC_FIREBASE_AUTH_EMULATOR_HOST) {
  const emulatorHost = process.env.NEXT_PUBLIC_FIREBASE_AUTH_EMULATOR_HOST;
  connectAuthEmulator(auth, `http://${emulatorHost}`);
}

// Export Firebase utilities and methods
export { app, db, auth, storage, signInWithPopup, doc, setDoc };

import { useState } from "react";

// ================================================================

/**
 * Compares two objects based on a specific property, returning a result for descending order.
 * 
 * @param {Object} a - First object to compare.
 * @param {Object} b - Second object to compare.
 * @param {string} orderBy - The property name to compare the objects by.
 * @returns {number} - Returns -1, 0, or 1 based on the comparison.
 */
export function descendingComparator(a, b, orderBy) {
  if (b[orderBy] < a[orderBy]) return -1;
  if (b[orderBy] > a[orderBy]) return 1;
  return 0;
}

/**
 * Returns a comparator function based on the sort order ("asc" or "desc").
 * 
 * @param {string} order - Sort order, either "asc" or "desc".
 * @param {string} orderBy - The property to sort by.
 * @returns {Function} - Comparator function to use for sorting.
 */
export function getComparator(order, orderBy) {
  return order === "desc"
    ? (a, b) => descendingComparator(a, b, orderBy)
    : (a, b) => -descendingComparator(a, b, orderBy);
}

/**
 * Sorts an array stably using a comparator function, maintaining the original order for equal elements.
 * 
 * @param {Array} array - The array to sort.
 * @param {Function} comparator - Comparator function for sorting.
 * @returns {Array} - The sorted array.
 */
export function stableSort(array, comparator) {
  const stabilizedThis = array.map((el, index) => [el, index]);
  stabilizedThis.sort((a, b) => {
    const order = comparator(a[0], b[0]);
    if (order !== 0) return order;
    return a[1] - b[1];
  });
  return stabilizedThis.map((el) => el[0]);
}

