/**
 * Custom hook for managing table data, sorting, pagination, and selection in a MUI (Material-UI) table.
 * 
 * @param {Object} props - Properties for the table data and default sorting options.
 * @param {Array} props.listData - The data to display in the table.
 * @param {string} props.defaultSort - The default column to sort by (default is "name").
 * @param {string} props.defaultOrder - The default sorting order ("asc" or "desc").
 * 
 * @returns {Object} - Returns state and functions for handling table behavior (sorting, pagination, selection).
 */
export default function useMuiTable(props) {
  const {
    listData = [],
    defaultSort = "name",
    defaultOrder = "asc"
  } = props;

  const [rowsPerPage] = useState(20); // Number of rows displayed per page
  const [page, setPage] = useState(0); // Current page number
  const [orderBy, setOrderBy] = useState(defaultSort); // The column to sort by
  const [selected, setSelected] = useState([]); // Selected rows
  const [order, setOrder] = useState(defaultOrder); // Sorting order (asc/desc)

  /**
   * Handles sorting when a column header is clicked.
   * @param {string} property - The column being sorted.
   */
  const handleRequestSort = (property) => {
    const isAsc = orderBy === property && order === "asc";
    setOrder(isAsc ? "desc" : "asc");
    setOrderBy(property);
  };

  /**
   * Handles selecting or deselecting all rows.
   * @param {boolean} checked - Whether all rows should be selected or deselected.
   * @param {string} defaultSelect - The property used to uniquely identify each row.
   */
  const handleSelectAllClick = (checked, defaultSelect) => {
    if (checked) {
      const newSelected = listData.map((item) => item[defaultSelect]);
      setSelected(newSelected);
      return;
    }
    setSelected([]);
  };

  /**
   * Handles selecting or deselecting a specific row.
   * @param {string} name - The name (or identifier) of the row to select or deselect.
   */
  const handleRowClick = (name) => {
    const selectedIndex = selected.indexOf(name);
    let newSelected = [];

    if (selectedIndex === -1) {
      newSelected = newSelected.concat(selected, name);
    } else if (selectedIndex === 0) {
      newSelected = newSelected.concat(selected.slice(1));
    } else if (selectedIndex === selected.length - 1) {
      newSelected = newSelected.concat(selected.slice(0, -1));
    } else if (selectedIndex > 0) {
      newSelected = newSelected.concat(
        selected.slice(0, selectedIndex),
        selected.slice(selectedIndex + 1)
      );
    }

    setSelected(newSelected);
  };

  /**
   * Handles changing the page of the table.
   * @param {number} newPage - The new page number.
   */
  const handleChangePage = (_, newPage) => setPage(newPage - 1);

  // Filter the data based on current page, sorting, and rows per page
  const filteredList = stableSort(listData, getComparator(order, orderBy)).slice(
    page * rowsPerPage,
    page * rowsPerPage + rowsPerPage
  );

  return {
    page,
    order,
    orderBy,
    selected,
    rowsPerPage,
    filteredList,
    handleRowClick,
    handleChangePage,
    handleRequestSort,
    handleSelectAllClick
  };
}

import { useRef, useState, useEffect } from "react";

/**
 * Custom hook to detect if an element overflows horizontally to the left or right side of the viewport.
 * This hook is useful for responsive layouts or detecting when content is outside the visible area.
 *
 * @returns {Object} - The reference to the element, overflow state, and the function to check for overflow.
 * @returns {Object} elementRef - A ref to attach to the DOM element you want to monitor.
 * @returns {boolean} isLeftOverflowing - Boolean indicating if the element is overflowing to the left.
 * @returns {boolean} isRightOverflowing - Boolean indicating if the element is overflowing to the right.
 * @returns {Function} checkOverflow - Function to manually trigger the overflow check.
 */
export default function useOverflowDetect() {
  const elementRef = useRef(null); // Reference to the element being monitored
  const [isLeftOverflowing, setLeftIsOverflowing] = useState(false); // State to track if the element is overflowing to the left
  const [isRightOverflowing, setRightIsOverflowing] = useState(false); // State to track if the element is overflowing to the right

  /**
   * Checks if the element is overflowing either to the left or right side of the viewport.
   */
  function checkOverflow() {
    const element = elementRef.current;

    if (element) {
      const elementRect = element.getBoundingClientRect(); // Get the element's position and size
      const viewportWidth = window.innerWidth || document.documentElement.clientWidth; // Get the viewport width

      // Check for left overflow
      if (elementRect.left < 0) setLeftIsOverflowing(true);
      else setLeftIsOverflowing(false);

      // Check for right overflow
      if (elementRect.right > viewportWidth) setRightIsOverflowing(true);
      else setRightIsOverflowing(false);
    }
  }

  useEffect(() => {
    // Add resize event listener to check for overflow on window resize
    window.addEventListener("resize", checkOverflow);
    checkOverflow(); // Run initial overflow check when the component is mounted

    // Clean up the event listener on component unmount
    return () => {
      window.removeEventListener("resize", checkOverflow);
    };
  }, []);

  return {
    elementRef,           // Ref to attach to the DOM element
    isRightOverflowing,    // Boolean: true if the element is overflowing to the right
    isLeftOverflowing,     // Boolean: true if the element is overflowing to the left
    checkOverflow          // Function to manually check for overflow
  };
}

import { useCallback, useEffect, useState } from "react";

/**
 * Custom hook to detect when the user has scrolled past a certain point and fix the header or any element
 * at the top of the viewport. The hook monitors the vertical scroll position and compares it with the 
 * height of a referenced element.
 *
 * @param {Object} ref - A React reference to the element being monitored for scroll position.
 * @returns {Object} - An object containing `isFixedHeader`, a boolean indicating if the header should be fixed.
 */
export default function useScroller(ref) {
  const [isFixedHeader, setIsFixedHeader] = useState(false); // State to track whether the header is fixed

  /**
   * Function to check the scroll position and determine whether the header should be fixed.
   */
  const scroller = useCallback(() => {
    let postionHeight = 0;

    // Get the offset of the referenced element
    if (ref.current) {
      postionHeight = ref.current.offsetTop + ref.current.offsetHeight;
    }

    // Fix the header if the scroll position exceeds the element's position
    if (postionHeight && window.pageYOffset > postionHeight) {
      setIsFixedHeader(true);
      return;
    }

    // Unfix the header if the scroll position is above the element
    setIsFixedHeader(false);
  }, [ref]);

  useEffect(() => {
    // Add scroll event listener when the component mounts
    if (!window) return;
    window.addEventListener("scroll", scroller);

    // Clean up the event listener when the component unmounts
    return () => window.removeEventListener("scroll", scroller);
  }, [scroller]);

  return {
    isFixedHeader // Boolean indicating if the header is fixed
  };
}

